  private readonly _body: d3.Selection<HTMLBodyElement | null, unknown, null, undefined>
  private readonly _svg: d3.Selection<SVGSVGElement, unknown, null, undefined>

  constructor () {
    const dom = new JSDOM('<!DOCTYPE html><body></body>')
    this._body = d3.select(dom.window.document.querySelector('body'))

    this._svg = this._body.append('svg')
      .attr('xmlns', 'http://www.w3.org/2000/svg')
  }

